function sleep(ms){return new Promise(resolve=>setTimeout(resolve,ms))}async function popText(element,ms){for(;0!=element.innerText.length;)element.innerText=element.innerText.slice(0,-1),await sleep(ms)}async function insertText(element,text_from,ms){let text="";for(let i of[...text_from])text+=i,element.innerText=text,await sleep(ms)}(async()=>{let config_initialSleep=5e3,config_operationSleep=50,config_afterPopingSleep=1e3,config_afterInsertingSleep=2e3,config_texts=["a Student","a Junior Web Developer","looking for a new and exciting job"];await sleep(config_initialSleep);for(let x of config_texts){let element=document.getElementById("text_to_change");await popText(element,config_operationSleep),await sleep(config_afterPopingSleep),await insertText(element,x,config_operationSleep),await sleep(config_afterInsertingSleep)}})();